void clear_bird(uint16_t x, uint16_t y, uint16_t bg ){
	int i, j;
	for (i = 0; i < 24; i++){
		for (j=0; j<34; j++){
			LCD_SetPoint(x+j, y+i, bg);
		}
	}
}

void draw_bird(uint16_t x, uint16_t y){
	int i, j;
	for (i = 0; i < 24; i++){
		for (j=0; j<34; j++){
			LCD_SetPoint(x+j, y+i, bird_image[i][j]);
		}
	}
}

void emit_tone(unsigned int intensity){
	disable_timer(2);
	reset_timer(2);
	init_timer(2,intensity);
	ticks=0;
	num_sin=0;
	enable_timer(2);
}
