void clear_bird(uint16_t x, uint16_t y, uint16_t bg ){
	int i, j;
	for (i = 0; i < 24; i++){
		for (j=0; j<34; j++){
			LCD_SetPoint(x+j, y+i, bg);
		}
	}
}

void draw_bird(uint16_t x, uint16_t y){
	int i, j;
	for (i = 0; i < BIRD_HEIGHT; i++){
		for (j=0; j<BIRD_WIDTH; j++){
			if (bird_image[BIRD_HEIGHT*bird_sequence + i][j]==0x0)
				LCD_SetPoint(x+j, y+i, BG_COLOR);
			else
				LCD_SetPoint(x+j, y+i, bird_image[BIRD_HEIGHT*bird_sequence + i][j]);
		}
	}
	bird_sequence = (bird_sequence+1) % 4;
}

void clear_pipe(uint16_t x, uint16_t y){
	LCD_DrawRect(x, 0, PIPE_WIDTH, y, BG_COLOR);
	LCD_DrawRect(x, y+PIPE_HOLE_HEIGHT, PIPE_WIDTH, MAX_Y-BOTTOM_SPACE-PIPE_HOLE_HEIGHT-y, BG_COLOR);
}

void draw_pipe(uint16_t x, uint16_t y){
	// y is the starting y coordinate of the pipe hole
	int i, j;
	for (i = 0; i < MAX_Y-BOTTOM_SPACE; i++){
		for (j=0; j<PIPE_WIDTH; j++){
			if (i < y || i > y+PIPE_HOLE_HEIGHT)
				LCD_SetPoint(x+j, i, pipe[j]);
			else if (i==y || i== y+PIPE_HOLE_HEIGHT)
				LCD_SetPoint(x+j, i, 0x51c8);
		}
	}
}

void emit_tone(unsigned int intensity){
	disable_timer(2);
	reset_timer(2);
	init_timer(2,intensity);
	enable_timer(2);
}
